mapper.select:
SELECT e.id matchId, e.title title, e.sub_title subTitle, e.match_kind kind, e.match_image_url matchImageUrl, e.match_status matchStatus, r.organizer_name organizer, ( SELECT COUNT(*) FROM match_participant WHERE match_id = e.id AND `status` = '2' ) AS participantSum, u.nickname organizerName, ( SELECT DATE_FORMAT( FROM_DAYS( TO_DAYS(NOW()) - TO_DAYS(u.birthday) ), '%Y' ) + 0 ) AS organizerAge, u.sex organizerSex, u.credit organizerCredit, ( DATEDIFF(p.begin_time, NOW()) ) AS toBeginDay FROM match_event e LEFT JOIN match_review r ON r.organizer_kind = '0' AND r.match_id = e.id LEFT JOIN user u ON u.id = e.initiator_id LEFT JOIN match_progress p ON p.match_id = e.id AND p.sort = '0' + 0 WHERE 1 = 1
AND e.initiator_id = #{user_id}
AND e.id = #{match_id}
LIMIT #{page_count},#{page_size}
